import React, {Component} from 'react';
import '../bootstrap.min.css';
import '../App.css';
import { connect } from "react-redux"
import Pagination from "react-js-pagination"
// import axios from 'axios'

class Table extends Component {
  constructor(props){
    super(props)
    this.state = {
      showFunc : props.showFunc,
      showing : props.showing,
      path: props.path,
      data: [],
      keys: [],
      activePage : 1
    }
    this.handleHomeClick = this.handleHomeClick.bind(this)
    this.handlePageChange = this.handlePageChange.bind(this)
  }
  componentWillReceiveProps(nextProps){
    console.log(nextProps)  
    if(nextProps.showing && nextProps.data.length !== 0){
      this.setState({
        data : nextProps.data,
        keys : Object.keys(nextProps.data[0])
      })
    }
  }
  handlePageChange(pageNumber) {
    this.setState({activePage: pageNumber});
  }
  handleHomeClick(e) {
    e.preventDefault();
    this.setState({
      data : []
    })

    this.state.showFunc();
  }
  render() {
    let styleT = {};
    if(!this.props.showing)
      styleT = {display: "none"};
    return (
      <div style={styleT} className="">
          <button className="btn btn-primary back-btn" onClick={this.handleHomeClick}>Home</button>
          <table className="table table-bordered table-style table-hover">
            <tbody><tr>{this.state.keys.map((e, i) => {
              return (
                <th key={i}>{e}</th>
              )
            })}</tr>

                {this.state.data.map((e1, i) => {
                  return(<tr>
                    {this.state.keys.map((e2) => {
                      return (<td>{e1[e2]}</td>)
                    })}
                  </tr>)
                })}


            </tbody>
          </table>

          <Pagination
            activePage={this.state.activePage}
            itemsCountPerPage={10}
            totalItemsCount={450}
            pageRangeDisplayed={5}
            onChange={this.handlePageChange}
          />
      </div>
    )
  }
}

const mapStateToProps = state => {
  return { data: state.data }
}

export default connect(mapStateToProps, {})(Table);
