import React, {Component} from 'react';
import '../bootstrap.min.css';
import '../App.css';
import { connect } from "react-redux"
// import axios from 'axios'

class Table extends Component {
  constructor(props){
    super(props)
    this.state = {
      showFunc : props.showFunc,
      showing : props.showing,
      path: props.path,
      data: [],
      keys: []
    }
    this.handleHomeClick = this.handleHomeClick.bind(this)
  }
  componentWillReceiveProps(nextProps){
    console.log(nextProps)  
    if(nextProps.showing){
      console.log(Object.keys(nextProps.data[0]))
      this.setState({
        data : nextProps.data,
        // keys : Object.keys(nextProps.data[0])
      })
    }
  }

  handleHomeClick(e) {
    e.preventDefault();
    this.setState({
      data : []
    })

    this.state.showFunc();
  }
  render() {
    let styleT = {};
    if(!this.props.showing)
      styleT = {display: "none"};
    return (
      <div style={styleT} className="">
          <button className="btn btn-primary back-btn" onClick={this.handleHomeClick}>Home</button>
          <table className="table table-bordered table-style table-hover">
            <tbody><tr>{this.state.keys.map((e, i) => {
              return (
                <th key={i}>{e}</th>
              )
            })}</tr>
              {/* {subArr.map((e) => {
                return (
                  <ObjElements obj={e} arr={this.state.arrayOfObjProps} key={Math.random().toString(36).substr(2, 9)} />
                )
              })} */}

            </tbody>
          </table>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return { data: state.data }
}

export default connect(mapStateToProps, {})(Table);
