import React, {Component} from 'react';
import '../bootstrap.min.css';
import '../App.css';
import axios from 'axios'
class Table extends Component {
  constructor(props){
    super(props)
    this.state = {
      showFunc : props.showFunc,
      showing : props.showing,
      path: props.path,
      data: []
    }
    this.handleHomeClick = this.handleHomeClick.bind(this)
  }
  componentWillReceiveProps(nextProps){
    console.log(this.props.showing)
    console.log(this.state.showing)
    console.log(nextProps)  
    if(nextProps.showing){
      let newFileBody = {
        path: this.props.path,
        activePage: 1,
        sortBy: null,
        sorting: null,
        searchQuery: null
      }
      axios.post('/filedataoffset', newFileBody)
      .then((res)=>{
          this.inputRef.current.value = ''
          console.log(res.data)
      })
      .catch((err) => {
          console.log(err)
      })
    }
  }

  handleHomeClick(e) {
    e.preventDefault();
    // this.setState({
    //   activePage: 1,
    //   active100: 1,
    //   perPage: 5,
    //   path: '',
    //   data: [],
    //   sortBy: '_id',
    //   sorting: 'asc',
    //   searchQuery: null,
    // })

    this.state.showFunc();
  }
  render() {
    let styleT = {};
    if(!this.props.showing)
      styleT = {display: "none"};
    return (
      <div style={styleT} className="">
          <button className="btn btn-primary back-btn" onClick={this.handleHomeClick}>Home</button>
        <p>NESTO!</p>
      </div>
    )
  }
}

export default Table;
