import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css'
import '../App.css';
import { connect } from "react-redux"

// import axios from 'axios'

class Table extends Component {
  constructor(props){
    super(props)
    this.state = {
      showFunc : props.showFunc,
      showing : props.showing,
      path: props.path,
      data: [],
      keys: [],
      activePage : 1,
      perPage : 5,
      num1 : 1,
      num2 : 2,
      num3 : 3,
      num4 : 4,
      num5: 5,
      classNum1 : "page-item active",
      classNum2 : "page-item",
      classNum3 : "page-item",
      classNum4 : "page-item",
      classNum5 : "page-item",
    }
    this.handleHomeClick = this.handleHomeClick.bind(this)
    this.handlePageChange = this.handlePageChange.bind(this)
    this.handlePagingButtonClick = this.handlePagingButtonClick.bind(this)
  }
  componentWillReceiveProps(nextProps){
    console.log(nextProps)  
    if(nextProps.showing && nextProps.data.length !== 0){
      this.setState({
        data : nextProps.data,
        keys : Object.keys(nextProps.data[0])
      })
    }
  }
  handlePageChange(pageNumber) {
    this.setState({activePage: pageNumber});
  }
  handleHomeClick(e) {
    e.preventDefault()
    this.setState({
      data: [],
      keys: [],
      activePage : 1,
      perPage : 5
    })

    this.state.showFunc();
  }
  handlePagingButtonClick(e){
    e.preventDefault()
  }
  render() {
    let styleT = {};
    if(!this.props.showing)
      styleT = {display: "none"};


    const arr = this.state.data;
    const currPage = this.state.activePage;
    const perPage = this.state.perPage;

    let indeOfLast = currPage * perPage;
    let indexOfFirst = indeOfLast - perPage;
    const subArr = arr.slice(indexOfFirst, indeOfLast);
    return (
      <div style={styleT} className="">
          <button className="btn btn-primary back-btn" onClick={this.handleHomeClick}>Home</button>
          <table className="table table-bordered table-style table-hover">
            <tbody><tr>{this.state.keys.map((e, i) => {
              return (
                <th key={i}>{e}</th>
              )
            })}</tr>

                {subArr.map((e1, i) => {
                  return(<tr key={Math.random().toString(36).substr(2, 9)}>
                    {this.state.keys.map((e2) => {
                      return (<td key={Math.random().toString(36).substr(2, 9)}>{e1[e2]}</td>)
                    })}
                  </tr>)
                })}


            </tbody>
          </table>

          <nav aria-label="Page navigation example">
            <ul className="pagination">
              <li className="page-item"><button className="page-link" href="#">{'<<'}</button></li>
              <li className={this.state.classNum1}><button onClick={this.handlePagingButtonClick} className="page-link" href="#">{this.state.num1}</button></li>
              <li className={this.state.classNum2}><button onClick={this.handlePagingButtonClick} className="page-link" href="#">{this.state.num2}</button></li>
              <li className={this.state.classNum3}><button onClick={this.handlePagingButtonClick} className="page-link" href="#">{this.state.num3}</button></li>
              <li className={this.state.classNum4}><button onClick={this.handlePagingButtonClick} className="page-link" href="#">{this.state.num4}</button></li>
              <li className={this.state.classNum5}><button onClick={this.handlePagingButtonClick} className="page-link" href="#">{this.state.num5}</button></li>
              <li className="page-item"><button className="page-link" href="#">{'>>'}</button></li>
            </ul>
          </nav>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return { data: state.data }
}

export default connect(mapStateToProps, {})(Table);
